import React from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import locations from "../data/locations";
import { motion, AnimatePresence } from "framer-motion";

// Placeholder pages
const Home = () => (
  <div className="p-10 text-center">
    <h1 className="text-4xl font-bold mb-4">Welcome to the Shattered Veil</h1>
    <p className="text-lg text-gray-600">An interactive companion to your Far Realm-infused D&D campaign.</p>
  </div>
);


const Map = () => {
  const [selected, setSelected] = React.useState(null);

  return (
    <div className="p-10 relative overflow-hidden">
      <h2 className="text-2xl font-semibold mb-4">Interactive Map</h2>
      <div className="relative w-full max-w-4xl mx-auto border rounded-xl shadow-lg overflow-hidden">
        <img src="/Wmap.jpg" alt="Campaign Map" className="w-full" />

        {locations.map(loc => (
          <button
            key={loc.id}
            onClick={() => setSelected(loc)}
            className="absolute bg-teal-500 rounded-full w-4 h-4 border-2 border-white hover:scale-125 transition"
            style={{ top: loc.y, left: loc.x, transform: "translate(-50%, -50%)" }}
            title={loc.name}
          />
        ))}
      </div>

      <AnimatePresence>
        {selected && (
          <motion.aside
            initial={{ x: "100%" }}
            animate={{ x: 0 }}
            exit={{ x: "100%" }}
            transition={{ type: "spring", stiffness: 300, damping: 30 }}
            className="fixed right-0 top-0 h-full w-80 bg-white shadow-2xl p-6 z-50 overflow-y-auto"
          >
            <h3 className="text-xl font-bold mb-2">{selected.name}</h3>
            <p className="text-sm text-gray-700 mb-4">{selected.description}</p>

            <h4 className="text-md font-semibold">Quests</h4>
            <ul className="mb-4 list-disc list-inside text-sm">
              {selected.quests.map((q, i) => (
                <li key={i}>
                  {q.title} <span className="text-xs text-gray-500">({q.status})</span>
                </li>
              ))}
            </ul>

            <h4 className="text-md font-semibold">NPCs</h4>
            <ul className="list-disc list-inside text-sm">
              {selected.npcs.map((n, i) => <li key={i}>{n}</li>)}
            </ul>

            <button
              onClick={() => setSelected(null)}
              className="mt-6 px-3 py-1 text-sm bg-teal-500 text-white rounded hover:bg-teal-600"
            >
              Close
            </button>
          </motion.aside>
        )}
      </AnimatePresence>
    </div>
  );
};

const Characters = () => (
  <div className="p-10">
    <h2 className="text-2xl font-semibold mb-4">Party & NPCs</h2>
    <p>Character cards go here...</p>
  </div>
);

const Quests = () => (
  <div className="p-10">
    <h2 className="text-2xl font-semibold mb-4">Quest Log</h2>
    <p>Track your missions...</p>
  </div>
);

const RiftPressure = () => (
  <div className="p-10">
    <h2 className="text-2xl font-semibold mb-4">Rift Pressure</h2>
    <p>Track the Far Realm's influence...</p>
  </div>
);

const Merchants = () => (
  <div className="p-10">
    <h2 className="text-2xl font-semibold mb-4">Traveling Merchants</h2>
    <p>Dynamic merchant inventory...</p>
  </div>
);

const Journal = () => (
  <div className="p-10">
    <h2 className="text-2xl font-semibold mb-4">Lore Journal</h2>
    <p>Discover entries here...</p>
  </div>
);

const Nav = () => (
  <nav className="flex justify-between items-center p-5 bg-gray-900 text-white shadow-md">
    <h1 className="text-xl font-bold">The Shattered Veil</h1>
    <div className="space-x-4">
      <Link to="/" className="hover:text-teal-400">Home</Link>
      <Link to="/map" className="hover:text-teal-400">Map</Link>
      <Link to="/characters" className="hover:text-teal-400">Characters</Link>
      <Link to="/quests" className="hover:text-teal-400">Quests</Link>
      <Link to="/rift" className="hover:text-teal-400">Rift Pressure</Link>
      <Link to="/merchants" className="hover:text-teal-400">Merchants</Link>
      <Link to="/journal" className="hover:text-teal-400">Journal</Link>
    </div>
  </nav>
);

export default function App() {
  return (
    <Router>
      <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} className="min-h-screen bg-gray-100">
        <Nav />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/map" element={<Map />} />
          <Route path="/characters" element={<Characters />} />
          <Route path="/quests" element={<Quests />} />
          <Route path="/rift" element={<RiftPressure />} />
          <Route path="/merchants" element={<Merchants />} />
          <Route path="/journal" element={<Journal />} />
        </Routes>
      </motion.div>
    </Router>
  );
}
