import React from 'react';
import ReactMarkdown from 'react-markdown';

export default function ItemCard({ item }) {
  return (
    <div className="w-full max-w-sm bg-gradient-to-br from-gray-800 to-gray-900 rounded-2xl border border-gray-700 shadow-xl text-white p-4 relative">
      {/* Card Header */}
      <div className="border-b border-gray-600 pb-2 mb-2">
        <h3 className="text-xl font-bold tracking-wide uppercase">{item.name}</h3>
        <p className="text-sm text-gray-400 italic">
          {item.type} &mdash; {item.rarity}
        </p>
      </div>

      {/* Description */}
      <div className="text-sm leading-relaxed prose prose-sm prose-invert max-w-none mb-4">
        <ReactMarkdown>{item.description}</ReactMarkdown>
      </div>

      {/* Divider */}
      <div className="border-t border-gray-700 pt-2 text-xs flex justify-between text-gray-400">
        <span>Weight: {item.weight || 'N/A'}</span>
        <span>Cost: {item.cost || '—'}</span>
      </div>

      {/* Decorative Corner Tag */}
      <div className="absolute top-2 right-2 bg-indigo-700 text-xs px-2 py-0.5 rounded-full shadow-md">
        SItem
      </div>
    </div>
  );
}
