import React, { useEffect, useState } from 'react';
import { supabase } from '../supabaseClient';
import ItemCard from '../components/ItemCard';
import itemsData from '../../public/Items/items.json';
import fluffData from '../../public/Items/fluff-items.json';
import baseData from '../../public/Items/items-base.json';

export default function AdminDashboard() {
  const [user, setUser] = useState(null);
  const [role, setRole] = useState(null);
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState('');
  const [itemPreview, setItemPreview] = useState(null);
  const [filteredItems, setFilteredItems] = useState([]);

  const [filters, setFilters] = useState({ name: '', rarity: '', type: '' });

  useEffect(() => {
    const fetchUserData = async () => {
      const { data: { user } } = await supabase.auth.getUser();
      setUser(user);

      if (user) {
        const { data: profile } = await supabase
          .from('user_profiles')
          .select('role')
          .eq('id', user.id)
          .single();
        setRole(profile?.role);
      }

      const { data: userList } = await supabase.from('user_profiles').select('id, role');
      setUsers(userList || []);
    };

    fetchUserData();
  }, []);

  const mergeItemData = () => {
    const items = itemsData.map(item => {
      const fluff = fluffData.find(f => f.name === item.name);
      const base = baseData.find(b => b.name === item.name);
      return {
        name: item.name,
        type: item.type || base?.type || 'Unknown',
        rarity: item.rarity || 'Common',
        description: fluff?.entries?.join(' ') || item.entries?.join(' ') || 'No description available.',
        weight: item.weight ? `${item.weight} lb` : 'N/A',
        cost: item.value ? `${item.value} gp` : '‚Äî'
      };
    });
    return items;
  };

  useEffect(() => {
    const mergedItems = mergeItemData();
    setFilteredItems(mergedItems);
  }, []);

  const handleFilter = () => {
    const mergedItems = mergeItemData();
    const filtered = mergedItems.filter(i => {
      return (
        (!filters.name || i.name.toLowerCase().includes(filters.name.toLowerCase())) &&
        (!filters.rarity || i.rarity === filters.rarity) &&
        (!filters.type || i.type === filters.type)
      );
    });
    setFilteredItems(filtered);
  };

  const handleAssign = async () => {
    if (!selectedUser || !itemPreview) return alert('Select player and item.');

    const { error } = await supabase.from('inventory_items').insert([{
      user_id: selectedUser,
      item_id: `admin-${Date.now()}`,
      item_name: itemPreview.name,
      item_type: itemPreview.type,
      item_rarity: itemPreview.rarity,
      item_description: itemPreview.description,
      item_weight: itemPreview.weight,
      item_cost: itemPreview.cost
    }]);

    if (error) {
      console.error('Assignment error:', error);
      alert('Failed to assign item.');
    } else {
      alert('‚úÖ Item assigned to player!');
      setItemPreview(null);
    }
  };

  if (!user || role !== 'admin') {
    return <div className="p-6 text-red-500">Access denied. Admins only.</div>;
  }

  const uniqueRarities = [...new Set(filteredItems.map(i => i.rarity))];
  const uniqueTypes = [...new Set(filteredItems.map(i => i.type))];

  return (
    <div className="max-w-5xl mx-auto p-6 space-y-6">
      <h2 className="text-2xl font-bold">üßô Admin Dashboard</h2>

      {/* Filters */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <input
          placeholder="Search by name"
          value={filters.name}
          onChange={(e) => setFilters({ ...filters, name: e.target.value })}
          className="p-2 rounded border"
        />
        <select
          value={filters.rarity}
          onChange={(e) => setFilters({ ...filters, rarity: e.target.value })}
          className="p-2 rounded border"
        >
          <option value="">All Rarities</option>
          {uniqueRarities.map(r => <option key={r}>{r}</option>)}
        </select>
        <select
          value={filters.type}
          onChange={(e) => setFilters({ ...filters, type: e.target.value })}
          className="p-2 rounded border"
        >
          <option value="">All Types</option>
          {uniqueTypes.map(t => <option key={t}>{t}</option>)}
        </select>
      </div>

      <button
        onClick={handleFilter}
        className="bg-blue-600 px-4 py-2 text-white rounded hover:bg-blue-700"
      >
        üîç Apply Filters
      </button>

      {/* Item Selector and Preview */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div>
          <h3 className="font-semibold mb-2">Select Item:</h3>
          <ul className="h-80 overflow-y-auto space-y-2">
            {filteredItems.map(i => (
              <li
                key={i.name}
                onClick={() => setItemPreview(i)}
                className="p-2 rounded hover:bg-gray-100 cursor-pointer border"
              >
                <strong>{i.name}</strong> ‚Äî <span className="text-sm text-gray-600">{i.rarity}, {i.type}</span>
              </li>
            ))}
          </ul>
        </div>

        <div>
          <h3 className="font-semibold mb-2">üì¶ Preview</h3>
          {itemPreview ? <ItemCard item={itemPreview} /> : <p>No item selected.</p>}
        </div>
      </div>

      {/* Assign to Player */}
      <div>
        <label className="block font-semibold mb-1">Assign to Player:</label>
        <select
          value={selectedUser}
          onChange={(e) => setSelectedUser(e.target.value)}
          className="w-full p-2 rounded border"
        >
          <option value="">-- Select a Player --</option>
          {users.map(u => (
            <option key={u.id} value={u.id}>{u.id} ({u.role})</option>
          ))}
        </select>
      </div>

      <button
        onClick={handleAssign}
        disabled={!itemPreview || !selectedUser}
        className="bg-green-600 px-4 py-2 text-white rounded hover:bg-green-700 disabled:opacity-50"
      >
        üéØ Assign Item to Player
      </button>
    </div>
  );
}
