import React from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import { supabase } from "./supabaseClient";
import { motion, AnimatePresence } from "framer-motion";

// Placeholder pages
const Home = () => (
  <div className="p-10 text-center">
    <h1 className="text-4xl font-bold mb-4">Welcome to the Shattered Veil</h1>
    <p className="text-lg text-gray-600">An interactive companion to your Far Realm-infused D&D campaign.</p>
  </div>
);


const initialLocations = [
  {
    id: 1,
    name: "Shradel",
    x: "40%",
    y: "30%",
    description: "The city of endless secrets.",
    quests: [{ title: "Shadows Over Shradel", status: "Active" }],
    npcs: ["High Archivist Malven"]
  },
  {
    id: 2,
    name: "Grayhearth",
    x: "60%",
    y: "70%",
    description: "Dwarven capital deep in the mountains.",
    quests: [],
    npcs: ["Thane Brokk Emberforge"]
  }
];

const Map = () => {
  const [locations, setLocations] = React.useState([]);
  const [selected, setSelected] = React.useState(null);
  const mapRef = React.useRef(null);

  // ðŸ§² Load markers from Supabase
  React.useEffect(() => {
    const fetchLocations = async () => {
      const { data, error } = await supabase.from("locations").select("*");
      if (error) console.error("Error loading locations:", error);
      else setLocations(data);
    };
    fetchLocations();
  }, []);

  const handleMapClick = async (e) => {
    const rect = mapRef.current.getBoundingClientRect();
    const x = ((e.clientX - rect.left) / rect.width) * 100;
    const y = ((e.clientY - rect.top) / rect.height) * 100;

    const name = prompt("Enter location name:");
    if (!name) return;
    const description = prompt("Enter description (optional):") || "";

    const newLocation = {
      name,
      x: `${x}%`,
      y: `${y}%`,
      description,
      quests: [],
      npcs: []
    };

    const { data, error } = await supabase.from("locations").insert([newLocation]).select();
    if (error) {
      console.error("Error adding marker:", error);
    } else {
      setLocations((prev) => [...prev, data[0]]);
    }
  };

  const handleDelete = async () => {
    if (!selected) return;
    const { error } = await supabase.from("locations").delete().eq("id", selected.id);
    if (error) {
      console.error("Error deleting marker:", error);
    } else {
      setLocations((prev) => prev.filter((loc) => loc.id !== selected.id));
      setSelected(null);
    }
  };

  return (
    <div className="p-10 relative overflow-hidden">
      <h2 className="text-2xl font-semibold mb-4">Interactive Map</h2>
      <div
        ref={mapRef}
        onClick={handleMapClick}
        className="relative w-full max-w-4xl mx-auto border rounded-xl shadow-lg overflow-hidden cursor-crosshair"
      >
        <img src="/Wmap.jpg" alt="Campaign Map" className="w-full" />

        {locations.map((loc) => (
          <button
            key={loc.id}
            onClick={(e) => {
              e.stopPropagation();
              setSelected(loc);
            }}
            className="absolute bg-red-50 rounded-md w-2 h-2 border border-black hover:scale-110 transition"
            style={{
              top: loc.y,
              left: loc.x,
              transform: "translate(-50%, -50%)"
            }}
            title={loc.name}
          />
        ))}
      </div>

      <AnimatePresence>
        {selected && (
          <motion.aside
            initial={{ x: "100%" }}
            animate={{ x: 0 }}
            exit={{ x: "100%" }}
            transition={{ type: "spring", stiffness: 300, damping: 30 }}
            className="fixed right-0 top-0 h-full w-80 bg-white shadow-2xl p-6 z-50 overflow-y-auto"
          >
            <h3 className="text-xl font-bold mb-2">{selected.name}</h3>
            <p className="text-sm text-gray-700 mb-4">{selected.description}</p>

            <h4 className="text-md font-semibold">Quests</h4>
            <ul className="mb-4 list-disc list-inside text-sm">
              {selected.quests?.length > 0 ? (
                selected.quests.map((q, i) => (
                  <li key={i}>
                    {q.title}{" "}
                    <span className="text-xs text-gray-500">({q.status})</span>
                  </li>
                ))
              ) : (
                <li className="text-gray-400">No quests</li>
              )}
            </ul>

            <h4 className="text-md font-semibold">NPCs</h4>
            <ul className="list-disc list-inside text-sm">
              {selected.npcs?.length > 0 ? (
                selected.npcs.map((n, i) => <li key={i}>{n}</li>)
              ) : (
                <li className="text-gray-400">No known NPCs</li>
              )}
            </ul>

            <div className="mt-6 flex gap-2">
              <button
                onClick={() => setSelected(null)}
                className="px-3 py-1 text-sm bg-teal-500 text-white rounded hover:bg-teal-600"
              >
                Close
              </button>
              <button
                onClick={handleDelete}
                className="px-3 py-1 text-sm bg-red-600 text-white rounded hover:bg-red-700"
              >
                Delete Marker
              </button>
            </div>
          </motion.aside>
        )}
      </AnimatePresence>
    </div>
  );
}; 
