// --- ItemCard.js ---
import React from 'react';

const rarityColors = {
  Common: 'border-gray-400 bg-gray-50',
  Uncommon: 'border-green-600 bg-green-50',
  Rare: 'border-red-600 bg-red-50',
  'Very Rare': 'border-purple-600 bg-purple-50',
  Legendary: 'border-yellow-500 bg-yellow-50',
  Artifact: 'border-blue-800 bg-blue-50',
  Homebrew: 'border-lime-700 bg-lime-50',
};

export default function ItemCard({ item }) {
  const colorClass = rarityColors[item.rarity] || 'border-gray-300 bg-white';
  return (
    <div className={`w-full p-4 border-4 rounded-md shadow-md ${colorClass} relative`}>
      <div className="text-xl font-bold text-center border-b border-black mb-1 pb-1">
        {item.name}
      </div>

      <div className="text-sm italic text-center text-gray-700 mb-2 border-b border-dotted pb-2 px-2">
        {item.flavor || 'This item has no flavor text.'}
      </div>

      {item.img && (
        <div className="absolute top-4 right-4 w-16 h-16">
          <img src={item.img} alt={item.name} className="object-contain w-full h-full border rounded" />
        </div>
      )}

      <div className="text-sm text-black px-2 whitespace-pre-line">
        {item.description}
      </div>

      <div className="flex justify-between items-center text-xs mt-4 px-2 pt-2 border-t border-black">
        <div className="font-semibold">{item.type} {item.slot && `— ${item.slot}`}</div>
        <div className="text-right text-gray-600 italic">{item.rarity}</div>
      </div>
    </div>
  );
}
